import React, { useEffect, useState } from "react";
import LeaveTable from "../components/LeaveTable";
import axios from "../api";

function ManagerApprovalPage() {
  const [pending, setPending] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchLeaves = async () => {
    setLoading(true);
    try {
      const res = await axios.get("/leaves/pending");
      setPending(res.data);
    } catch (err) {
      setPending([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchLeaves();
  }, []);

  // Updated: Use correct PUT endpoint, send empty object as body or managerComment only if needed, update UI after
  const handleDecision = async (leaveId, isApprove, managerComment = "") => {
    setLoading(true);
    const action = isApprove ? "approve" : "reject";
    try {
      await axios.put(`/leaves/${leaveId}/${action}`, managerComment ? { managerComment } : {});
      await fetchLeaves();
    } catch (error) {
      // optional: handle error, show toast etc.
    }
    setLoading(false);
  };

  return (
    <div className="dashboard-section">
      <h2>Pending Leave Requests</h2>
      <LeaveTable leaveRequests={pending} showActions onAction={handleDecision} />
      {loading && <div>Loading...</div>}
    </div>
  );
}

export default ManagerApprovalPage;
