import React, { useState, useEffect } from "react";
import axios from "../api/axios";

const ManagerApprovalPage = () => {
  const [requests, setRequests] = useState([]);
  const [comment, setComment] = useState("");

  useEffect(() => {
    axios.get("/leaves/pending").then(res => setRequests(res.data));
  }, []);

  function approve(id) {
    axios.put(`/leaves/${id}/approve`, { comment }).then(() => {
      setRequests(requests.filter(r => r.id !== id));
      setComment("");
    });
  }

  function reject(id) {
    axios.put(`/leaves/${id}/reject`, { comment }).then(() => {
      setRequests(requests.filter(r => r.id !== id));
      setComment("");
    });
  }

  return (
    <div>
      <h2>Pending Leave Requests</h2>
      <table>
        <thead>
          <tr><th>Employee</th><th>Type</th><th>Date</th><th>Notes</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {requests.map(r => (
            <tr key={r.id}>
              <td>{r.employee.name}</td>
              <td>{r.leaveType.type}</td>
              <td>{r.startDate} - {r.endDate}</td>
              <td>{r.notes}</td>
              <td>
                <input value={comment} onChange={e => setComment(e.target.value)} placeholder="Comment"/>
                <button onClick={() => approve(r.id)}>Approve</button>
                <button onClick={() => reject(r.id)}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManagerApprovalPage;
