import React, { useEffect, useState } from "react";
import axios from "../api/axios";
import { Link } from "react-router-dom";

const EmployeeDashboard = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const [balance, setBalance] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    axios.get(`/balance/${user.id}`).then((res) => setBalance(res.data));
    axios.get(`/leaves/employee/${user.id}`).then((res) => setHistory(res.data));
  }, [user.id]);

  return (
    <div>
      <h2>Welcome, {user.name}</h2>
      <h3>Your Leave Balances</h3>
      <ul>
        {balance.map((b) => (
          <li key={b.type}>{b.type}: {b.balance} days</li>
        ))}
      </ul>
      <Link to="/apply">Apply for Leave</Link>
      <h3>Your Leave History</h3>
      <table>
        <thead>
          <tr><th>Type</th><th>From</th><th>To</th><th>Status</th><th>Notes</th></tr>
        </thead>
        <tbody>
          {history.map(lr => (
            <tr key={lr.id}>
              <td>{lr.leaveType.type}</td>
              <td>{lr.startDate}</td>
              <td>{lr.endDate}</td>
              <td>{lr.status}</td>
              <td>{lr.notes}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default EmployeeDashboard;
