import React, { useEffect, useState } from "react";
import LeaveTable from "../components/LeaveTable";
import axios from "../api";

function ManagerApprovalPage() {
  const [pending, setPending] = useState([]);
  const [loading, setLoading] = useState(false);
  const [resultMsg, setResultMsg] = useState("");

  const fetchLeaves = async () => {
    setLoading(true);
    try {
      const res = await axios.get("/leaves/pending");
      setPending(res.data);
    } catch (err) {
      setPending([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchLeaves();
  }, []);

  const handleDecision = async (leaveId, isApprove, managerComment = "") => {
    setLoading(true);
    setResultMsg(""); // Clear previous messages
    const action = isApprove ? "approve" : "reject";
    try {
      console.log("API call to:", `/leaves/${leaveId}/${action}`, { managerComment });
      await axios.put(`/leaves/${leaveId}/${action}`, managerComment ? { managerComment } : {});
      setResultMsg(isApprove ? "Leave Approved!" : "Leave Rejected!");
      await fetchLeaves();
    } catch (error) {
      setResultMsg("Error updating leave. See console.");
      console.error(error);
    }
    setLoading(false);
  };

  return (
    <div className="dashboard-section">
      <h2>Pending Leave Requests</h2>
      {resultMsg && <div>{resultMsg}</div>}
      {loading ? (
        <div>Loading...</div>
      ) : pending.length > 0 ? (
        <LeaveTable leaveRequests={pending} showActions onAction={handleDecision} />
      ) : (
        <div>No pending leave requests.</div>
      )}
    </div>
  );
}

export default ManagerApprovalPage;
