import React from 'react';

function LeaveTable({ leaves, showActions, onApprove, onReject }) {
  return (
    <table className="elms-table">
      <thead>
        <tr>
          <th>Type</th>
          <th>Start</th>
          <th>End</th>
          <th>Status</th>
          <th>Reason</th>
          <th>Manager Comment</th>
          {showActions && <th>Actions</th>}
        </tr>
      </thead>
      <tbody>
        {leaves.map(lr => (
          <tr key={lr.id}>
            <td>{lr.leaveType?.type || lr.leaveTypeId}</td>
            <td>{lr.startDate}</td>
            <td>{lr.endDate}</td>
            <td>{lr.status}</td>
            <td>{lr.reason || lr.notes}</td>
            <td>{lr.managerComment}</td>
            {showActions && (
              <td>
                {lr.status === "PENDING" && (
                  <>
                  <button onClick={() => onApprove(lr.id)}>Approve</button>
                  <button onClick={() => onReject(lr.id)}>Reject</button>
                  </>
                )}
              </td>
            )}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
export default LeaveTable;
