import React, { useState, useEffect } from "react";
import axios from "../api/axios";
import { useNavigate } from "react-router-dom";

const ApplyLeaveForm = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const [types, setTypes] = useState([]);
  const [leaveTypeId, setLeaveTypeId] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [notes, setNotes] = useState("");
  const [err, setErr] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("/leave-types").then(res => setTypes(res.data));
  }, []);

  async function submit(e) {
    e.preventDefault();
    if (!leaveTypeId || !start || !end) {
      setErr("All fields required except notes."); return;
    }
    try {
      await axios.post("/leaves/apply", {
        leaveTypeId, startDate: start, endDate: end, notes
      });
      navigate("/dashboard");
    } catch (ex) {
      setErr(ex?.response?.data?.message || "Failed to apply");
    }
  }

  return (
    <form onSubmit={submit}>
      <h2>Apply for Leave</h2>
      <select value={leaveTypeId} onChange={e=>setLeaveTypeId(e.target.value)} required>
        <option value="">Select Leave Type</option>
        {types.map(t => 
          <option value={t.id} key={t.id}>{t.type}</option>
        )}
      </select>
      <input type="date" value={start} onChange={e=>setStart(e.target.value)} required />
      <input type="date" value={end} onChange={e=>setEnd(e.target.value)} required />
      <textarea value={notes} onChange={e=>setNotes(e.target.value)} placeholder="Notes (optional)"/>
      <button type="submit">Submit</button>
      {err && <p style={{color:"red"}}>{err}</p>}
    </form>
  );
};

export default ApplyLeaveForm;
